import React, { useEffect } from "react";
import styles from "./showTickets.module.css";

const ShowTickets = () => {
  useEffect(() => {
    let train = document.getElementById("seat-container");
    console.log("train", train);
    if (train) {
      for (let index = 1; index <= 80; index++) {
        if(index % 7 === 1) {
            let newRow = document.createElement('div');
            newRow.innerHTML = 'R';
            train.appendChild(newRow)
        }
        let seat = document.createElement("div");
        seat.classList.add(`${styles.seat}`);
        seat.innerHTML = index % 7;
        train.appendChild(seat);
        console.log('appended');
      }
    }

    return () => {
        train.innerHTML =  " ";
      }
  }, []);


  return <div className={styles.container} id="seat-container"></div>;
};

export default ShowTickets;
