import React, { useState, useEffect } from "react";
import styles from "./showTickets.module.css";

const ShowTickets = (props) => {
  const { setShowTrain, setShowInput, setShowTickets, bookedSeats } = props;
  useEffect(() => {
    let train = document.getElementById("seat-container");
    let rows = ["A", 'B','C','D','E','F','G', 'H', 'I', 'J', 'K', 'L'];
    let selectedSeats = ['A1', 'A4']
    // let rows = ["A1", 'A2','A3','A4','A5','A6','A7', 
    //  "B1", "B2", "B3", "B4", "B5", "B6", "B7", 'C1','C2','C3','C4','C5','C6','C7', '1','2','3','4'];
    let rowsIndex = 0;
    if (train) {
      for (let index = 1; index <= 80; index++) {
        if (index % 7 === 0) {
          rowsIndex++;
        }
        let seat = document.createElement("div");
        seat.classList.add(`${styles.seat}`);
        if (index % 7 === 0) {
          seat.innerHTML = `${rows[rowsIndex]}7`;
        } else {
          seat.innerHTML = `${rows[rowsIndex]}${index  % 7}`;
        }
        train.appendChild(seat);
      }
    }
    console.log(
      "ðŸš€ ~ file: ShowTickets.js:6 ~ ShowTickets ~ bookedSeats",
      bookedSeats
    );

    return () => {
      train.innerHTML = " ";
    };
  }, []);

  function bookingConfirmation() {
    setShowTickets(false);
    setShowTrain(true);
    setShowInput(true);
  }

  return (
    <div className={styles.wrapper}>
      <div className={styles.seatContainer} id="seat-container"></div>
      <button onClick={bookingConfirmation}>Confirm</button>
    </div>
  );
};

export default ShowTickets;
