import React, { useState, useEffect, useRef } from "react";
import styles from "./showTickets.module.css";

const ShowTickets = (props) => {
  const {
    input,
    setShowTrain,
    setShowInput,
    setShowTickets,
    noOfBookedSeats,
    bookingNumber,
    bookedSeats,
    seatsLeftInRow,
  } = props;

  function isBooked(currentSeat) {
    let ans = false;
    bookedSeats.current.forEach((seat) => {
      if (seat === currentSeat) {
        ans = true;
      }
    });
    return ans;
  }

  //   function getNoOfSeatsLeft(rowNumber) {
  //     return 7 - seatsLeftInRow.current[rowNumber];
  //   }

  function getRowsLeft() {
    let seats = document.querySelectorAll('.seat')
    let rowNumber = 0;
    let availableSeat = 7;
    seats.forEach((seat) => {
        if(seat.classList.contains(styles.selectedSeat)) {
            availableSeat--;
        }
        if(seat.innerHTML.charAt(1) === 7) {
            seatsLeftInRow.current[rowNumber] = availableSeat;
            rowNumber++;
            availableSeat = 7;
            console.log('rowNumber: ' + rowNumber);
        }
        console.log('yoo');
    })
  }

  useEffect(() => {
    let train = document.getElementById("seat-container");
    let rows = ["A", "B", "C", "D", "E", "F", "G", "H", "I", "J", "K", "L"];
    let noOfSeatsToBeBooked = input;

    let rowsIndex = -1;
    if (train) {
      for (let index = 1; index <= 80; index++) {
        if (index % 7 === 1) {
          rowsIndex++;
        }
        let seat = document.createElement("div");
        if (index % 7 === 0) {
          seat.innerHTML = `${rows[rowsIndex]}7`;
        } else {
          seat.innerHTML = `${rows[rowsIndex]}${index % 7}`;
        }

        // if (isBooked(seat.innerHTML) === true) {
        //   console.log("selected", seat.innerHTML);
        //   seat.classList.add(`${styles.bookedSeat}`);
        // } else if (getNoOfSeatsLeft(rowsIndex) >= noOfSeatsToBeBooked) {
        //     seat.classList.add(`${styles.selectedSeat}`);
        // } else {
        //   seat.classList.add(`${styles.emptySeat}`);
        // }

        if (isBooked(seat.innerHTML) === true) {
          console.log("selected", seat.innerHTML);
          seat.classList.add(`${styles.bookedSeat}`);
        } else if (
          bookingNumber.current === rowsIndex &&
          seat.innerHTML.charAt(1) <= input
        ) {
          seat.classList.add(`${styles.selectedSeat}`);
          console.log("adding", seat.innerHTML);
          bookedSeats.current.push(seat.innerHTML);
        } else {
          seat.classList.add(`${styles.emptySeat}`);
        }
        seat.classList.add('seat');
        train.appendChild(seat);
      }
      getRowsLeft();
      console.log(seatsLeftInRow.current);
    }
    // console.log(
    //   "ðŸš€ ~ file: ShowTickets.js:6 ~ ShowTickets ~ bookedSeats",
    //   noOfBookedSeats,
    //   bookingNumber.current
    // );

    return () => {
      train.innerHTML = " ";
    };
  }, []);

  function bookingConfirmation() {
    setShowTickets(false);
    setShowTrain(true);
    setShowInput(true);
  }

  return (
    <div className={styles.wrapper}>
      <div className={styles.seatContainer} id="seat-container"></div>
      <button onClick={bookingConfirmation}>Confirm</button>
    </div>
  );
};

export default ShowTickets;
