import React, { useState } from "react";
import Error from "../Error/Error";
import styles from "./input.module.css";

const Input = (props) => {
  const {
    setShowTrain,
    setShowInput,
    setShowTickets,
    setInput,
    noOfBookedSeats,
  } = props;

  const [showError, setShowError] = useState(false);
  let errorMessage = "";

  function removeTrain(e) {
    e.preventDefault();
    let input = parseInt(document.getElementById("input").value);
    if (input < 1 || isNaN(input)) {
      errorMessage = "Please enter number of tickets you want to book.";
      setShowError(true);
    } else if (input > 7) {
      errorMessage = "You can reserve at most 7 tickets.";
      setShowError(true);
    } else if (noOfBookedSeats === 80) {
      errorMessage = "Sorry, this train is full.";
      setShowError(true);
    } else if (noOfBookedSeats + input > 80) {
      let availableSeat = 80 - noOfBookedSeats;
      let isOrAre = availableSeat === 1 ? "seat is" : "seats are";
      errorMessage = `Sorry, only ${80 - noOfBookedSeats} ${isOrAre} available.`;
      setShowError(true);
    } else {
      let train = document.getElementById("trainImg");
      if (train) {
        train.style.transform = "translateX(200%)";
      }
      setInput(input);

      setTimeout(() => {
        setShowInput(false);
        setShowTrain(false);
        setShowTickets(true);
      }, 1000);
    }
  }

  return (
    <div className={styles.container}>
      <form autoComplete="off">
        <input id="input" type="number" placeholder="Enter No Of Tickets" />
        <button onClick={removeTrain} type="submit">
          Book Ticket
        </button>
      </form>
      <Error errorMessage={errorMessage} />
    </div>
  );
};

export default Input;
