import React from "react";
import styles from "./input.module.css";

const Input = (props) => {
  const { setShowButton, setShowInput } = props;

  function removeTrain() {
    let train = document.getElementById("trainImg");
    if (train) {
      train.style.transform = "translateX(200%)";
    }

    setTimeout(() => {
      setShowInput(false);
      setShowButton(false);
    }, 1000);
  }

  return (
    <div className={styles.container}>
      <form autoComplete="off">
        <input id="input" type="number" placeholder="Enter No Of Tickets" />
        <button onClick={removeTrain} type="submit">Book Ticket</button>
      </form>
    </div>
  );
};

export default Input;
