import React from "react";
import styles from "./input.module.css";

const Input = (props) => {
  const { setShowTrain, setShowInput, setShowTickets, setInput } = props;

  function removeTrain(e) {
    e.preventDefault()
    let input = document.getElementById('input').value;
    console.log(input);
    if(input === 0 || input === "") {
        alert('Please enter number of tickets you want to book.')
        return;
    }
    let train = document.getElementById("trainImg");
    if (train) {
      train.style.transform = "translateX(200%)";
    }
    setInput(input);

    setTimeout(() => {
      setShowInput(false);
      setShowTrain(false);
      setShowTickets(true);
    }, 1000);
  }

  return (
    <div className={styles.container}>
      <form autoComplete="off">
        <input id="input" type="number" placeholder="Enter No Of Tickets" />
        <button onClick={removeTrain} type="submit">Book Ticket</button>
      </form>
    </div>
  );
};

export default Input;
