import React, { useState, useEffect } from "react";
import trainImg from "../../Images/train2-removebg-preview.png";
import styles from "./train.module.css";

const Train = () => {
//   useEffect(() => {
//     let train = document.getElementById("trainImg");
//     if (train) {
//       train.style.transform = "translateX(0%)";
//     }

//     return () => {
//       let train = document.getElementById("trainImg");
//       if (train) {
//         train.style.transform = "translateX(200%)";
//       }
//     };
//   }, []);

  const [toggle, setToggle] = useState("true");

  const toggleHandler = () => {
    if (toggle) {
      let train = document.getElementById("trainImg");
      if (train) {
        train.style.transform = "translateX(200%)";
      }
    } else {
      let train = document.getElementById("trainImg");
      if (train) {
        train.style.transform = "translateX(0%)";
      }
    }
    setToggle(!toggle);
  };

  return (
    <div className={styles.train}>
      <button onClick={toggleHandler}>Train</button>
      <img src={trainImg} alt="Train" id="trainImg" />
    </div>
  );
};

export default Train;
